customStyles:
/* BEGIN: Abgeschnittene Buchstaben (Umbrüche) verhindern */
.product-info .product-info-details .product-info-title-desktop, .productlist-viewmode-grid .product-container .product-tile .title-description .title a, .productlist-swiper .product-container .product-tile .title-description .title a, .product-container.flyover .product-tile .title-description .title a {
hyphens: none;
word-break: normal;
}
/* END: Abgeschnittene Buchstaben (Umbrüche) verhindern */

/* BEGIN: Wagislider (Ersatz TeaserSlider) */
.wagislider {
  position: relative;
  overflow: hidden;
  width: 100%;
  padding-top: 20%;
}

.wagisliderimage {
  background: url(/images/slider_images/Stoffslider.jpg);
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
  position: absolute;
  left: 0;
  top: 0;
  padding-top: 30%;
  width: 900%;
  animation: wagislider 96s linear infinite;
}

.wagisliderlogo {
  position: absolute;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 1;
}

.wagisliderlogo img {
  opacity: 0.5;
  width: 100%;
  object-fit: contain;
}

@keyframes wagislider {
  0%	{ left: 0; }
  100%	{ left: -800%; }
}
/* END: Wagislider (Ersatz TeaserSlider) */

/* BEGIN: Sticky Button "Kostenpflichtig bestellen" */
.wagipay {
  padding: 3px;
  float: right;
  background-color: red;
  top: 30%;
  right: 0;
  position: sticky;
  z-index: 99;
}
/* END: Sticky Button "Kostenpflichtig bestellen" */

/* BEGIN: Steuerung (Punkte & Pfeile) im Teaser-Slider verbergen */
#stage {
    #slider {
        .swiper-pagination,
        .js-teaser-slider-prev,
        .js-teaser-slider-next {
            display: none;
        }
     }
}
/* END: Steuerung (Punkte & Pfeile) im Teaser-Slider verbergen */
